import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Jumbo from 'react-bootstrap/Jumbotron'
import Image from 'react-bootstrap/Image'
import Button from 'react-bootstrap/Button'
import Dia from './Dia'

export default function Mes(props) {
    const { nombreMes, cantidadDias, vecColores } = props
    var dias = new Array(cantidadDias);
    var i = -1;
     console.log(dias) 
    // dias.push( <Dia numeroDia={i++} colorFondo={vecColores[i]} />)
    dias.map((dia) => {
        return i++
    })
     console.log(dias) 

    return (
        <>
            <div>
                <h1>{nombreMes}</h1>
                <Container>
                    <Row xs="5" sm="5" md="5" lg="5" xl="5">

                        {dias.map(dia => {
                            console.log(dia)

                            return <Dia numeroDia={i++} colorFondo={vecColores[i]} />
                        })}
                        {dias}
                    </Row>
                </Container>
            </div>
        </>

    )
}
